mapper.update:
UPDATE auth_authorization.CAS_ORG
ORG_CODE = #{orgCode,jdbcType=VARCHAR}, ORG_NAME = #{orgName,jdbcType=VARCHAR}, PARENT_ID = #{parentId,jdbcType=BIGINT}, IS_LEAF = #{isLeaf,jdbcType=TINYINT}, DESCRIPTION = #{description,jdbcType=VARCHAR}, SECONDLEADER = #{secondleader,jdbcType=VARCHAR}, LEADER = #{leader,jdbcType=VARCHAR}, HIRES = #{hires,jdbcType=INTEGER}, ORG_ID_PATH = #{orgIdPath,jdbcType=VARCHAR}, ORG_PATH = #{orgPath,jdbcType=VARCHAR}, ORDERS = #{orders,jdbcType=INTEGER}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE auth_authorization.CAS_ORG
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM auth_authorization.CAS_ORG
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM auth_authorization.CAS_ORG


		AND ID = #{id,jdbcType=INTEGER}


		AND ORG_CODE = #{orgCode,jdbcType=VARCHAR}


		AND ORG_NAME = #{orgName,jdbcType=VARCHAR}


		AND PARENT_ID = #{parentId,jdbcType=BIGINT}


		AND IS_LEAF = #{isLeaf,jdbcType=TINYINT}


		AND DESCRIPTION = #{description,jdbcType=VARCHAR}


		AND SECONDLEADER = #{secondleader,jdbcType=VARCHAR}


		AND LEADER = #{leader,jdbcType=VARCHAR}


		AND HIRES = #{hires,jdbcType=INTEGER}


		AND ORG_ID_PATH = #{orgIdPath,jdbcType=VARCHAR}


		AND ORG_PATH = #{orgPath,jdbcType=VARCHAR}


		AND ORDERS = #{orders,jdbcType=INTEGER}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


  FROM auth_authorization.CAS_ORG


		AND ID = #{id,jdbcType=INTEGER}


		AND ORG_CODE = #{orgCode,jdbcType=VARCHAR}


		AND ORG_NAME = #{orgName,jdbcType=VARCHAR}


		AND PARENT_ID = #{parentId,jdbcType=BIGINT}


		AND IS_LEAF = #{isLeaf,jdbcType=TINYINT}


		AND DESCRIPTION = #{description,jdbcType=VARCHAR}


		AND SECONDLEADER = #{secondleader,jdbcType=VARCHAR}


		AND LEADER = #{leader,jdbcType=VARCHAR}


		AND HIRES = #{hires,jdbcType=INTEGER}


		AND ORG_ID_PATH = #{orgIdPath,jdbcType=VARCHAR}


		AND ORG_PATH = #{orgPath,jdbcType=VARCHAR}


		AND ORDERS = #{orders,jdbcType=INTEGER}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

 WHERE DELETED = "F"
