mapper.update:
UPDATE auth_authorization.CAS_ROLE
ROLE_NAME = #{roleName,jdbcType=VARCHAR}, ROLE_DESC = #{roleDesc,jdbcType=VARCHAR}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE auth_authorization.CAS_ROLE
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM auth_authorization.CAS_ROLE
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM auth_authorization.CAS_ROLE


		AND ID = #{id,jdbcType=INTEGER}


		AND ROLE_NAME like CONCAT('%', #{roleName,jdbcType=VARCHAR} ,'%')


		AND ROLE_DESC = #{roleDesc,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


  FROM auth_authorization.CAS_ROLE


		AND ID = #{id,jdbcType=INTEGER}


		AND ROLE_NAME = #{roleName,jdbcType=VARCHAR}


		AND ROLE_DESC = #{roleDesc,jdbcType=VARCHAR}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

 WHERE DELETED = "F"
