mapper.update:
UPDATE auth_authorization.CAS_USER
HEAD_IMG = #{headImg,jdbcType=VARCHAR}, ORG_ID = #{orgId,jdbcType=BIGINT}, ACCOUNT = #{account,jdbcType=VARCHAR}, EMP_NO = #{empNo,jdbcType=VARCHAR}, CUSTOM_NO = #{customNo,jdbcType=VARCHAR}, SUPP_NO = #{suppNo,jdbcType=VARCHAR}, NAME = #{name,jdbcType=VARCHAR}, PHONE = #{phone,jdbcType=VARCHAR}, EMAIL = #{email,jdbcType=VARCHAR}, PASSWORD = #{password,jdbcType=VARCHAR}, SLAT = #{slat,jdbcType=VARCHAR}, POSITION = #{position,jdbcType=VARCHAR}, IS_ABLE = #{isAble,jdbcType=TINYINT}, IS_ADMIN = #{isAdmin,jdbcType=TINYINT}, IS_CONTACTS = #{isContacts,jdbcType=TINYINT}, IS_FROST = #{isFrost,jdbcType=TINYINT}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR}, ABLE_TIME = #{ableTime,jdbcType=TIMESTAMP}, DISABLE_TIME = #{disableTime,jdbcType=TIMESTAMP}, FROST_TIME = #{frostTime,jdbcType=TIMESTAMP},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE auth_authorization.CAS_USER
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM auth_authorization.CAS_USER
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM auth_authorization.CAS_USER


		AND ID = #{id,jdbcType=INTEGER}


		AND HEAD_IMG = #{headImg,jdbcType=VARCHAR}


		AND ORG_ID = #{orgId,jdbcType=BIGINT}


		AND ACCOUNT = #{account,jdbcType=VARCHAR}


		AND EMP_NO = #{empNo,jdbcType=VARCHAR}


		AND CUSTOM_NO = #{customNo,jdbcType=VARCHAR}


		AND SUPP_NO = #{suppNo,jdbcType=VARCHAR}


		AND NAME = #{name,jdbcType=VARCHAR}


		AND PHONE = #{phone,jdbcType=VARCHAR}


		AND EMAIL = #{email,jdbcType=VARCHAR}


		AND PASSWORD = #{password,jdbcType=VARCHAR}


		AND SLAT = #{slat,jdbcType=VARCHAR}


		AND POSITION = #{position,jdbcType=VARCHAR}


		AND IS_ABLE = #{isAble,jdbcType=TINYINT}


		AND IS_ADMIN = #{isAdmin,jdbcType=TINYINT}


		AND IS_CONTACTS = #{isContacts,jdbcType=TINYINT}


		AND IS_FROST = #{isFrost,jdbcType=TINYINT}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


		AND ABLE_TIME = #{ableTime,jdbcType=TIMESTAMP}


		AND DISABLE_TIME = #{disableTime,jdbcType=TIMESTAMP}


		AND FROST_TIME = #{frostTime,jdbcType=TIMESTAMP}


  FROM auth_authorization.CAS_USER


		AND ID = #{id,jdbcType=INTEGER}


		AND HEAD_IMG = #{headImg,jdbcType=VARCHAR}


		AND ORG_ID = #{orgId,jdbcType=BIGINT}


		AND ACCOUNT = #{account,jdbcType=VARCHAR}


		AND EMP_NO = #{empNo,jdbcType=VARCHAR}


		AND CUSTOM_NO = #{customNo,jdbcType=VARCHAR}


		AND SUPP_NO = #{suppNo,jdbcType=VARCHAR}


		AND NAME = #{name,jdbcType=VARCHAR}


		AND PHONE = #{phone,jdbcType=VARCHAR}


		AND EMAIL = #{email,jdbcType=VARCHAR}


		AND PASSWORD = #{password,jdbcType=VARCHAR}


		AND SLAT = #{slat,jdbcType=VARCHAR}


		AND POSITION = #{position,jdbcType=VARCHAR}


		AND IS_ABLE = #{isAble,jdbcType=TINYINT}


		AND IS_ADMIN = #{isAdmin,jdbcType=TINYINT}


		AND IS_CONTACTS = #{isContacts,jdbcType=TINYINT}


		AND IS_FROST = #{isFrost,jdbcType=TINYINT}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


		AND ABLE_TIME = #{ableTime,jdbcType=TIMESTAMP}


		AND DISABLE_TIME = #{disableTime,jdbcType=TIMESTAMP}


		AND FROST_TIME = #{frostTime,jdbcType=TIMESTAMP}

 WHERE DELETED = "F"
