mapper.update:
UPDATE CMS_TEMPLATE_INFO
ALIAS = #{alias,jdbcType=VARCHAR}, MODEL_ID = #{modelId,jdbcType=BIGINT}, TEMPLATE_PATH = #{templatePath,jdbcType=VARCHAR}, ORDERS = #{orders,jdbcType=INTEGER}, IS_ABLE = #{isAble,jdbcType=TINYINT}, ABLE_TIME = #{ableTime,jdbcType=TIMESTAMP}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE CMS_TEMPLATE_INFO
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM CMS_TEMPLATE_INFO
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM CMS_TEMPLATE_INFO


		AND ID = #{id,jdbcType=INTEGER}


		AND ALIAS = #{alias,jdbcType=VARCHAR}


		AND MODEL_ID = #{modelId,jdbcType=BIGINT}


		AND TEMPLATE_PATH = #{templatePath,jdbcType=VARCHAR}


		AND ORDERS = #{orders,jdbcType=INTEGER}


		AND IS_ABLE = #{isAble,jdbcType=TINYINT}


		AND ABLE_TIME = #{ableTime,jdbcType=TIMESTAMP}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


  FROM CMS_TEMPLATE_INFO


		AND ID = #{id,jdbcType=INTEGER}


		AND ALIAS = #{alias,jdbcType=VARCHAR}


		AND MODEL_ID = #{modelId,jdbcType=BIGINT}


		AND TEMPLATE_PATH = #{templatePath,jdbcType=VARCHAR}


		AND ORDERS = #{orders,jdbcType=INTEGER}


		AND IS_ABLE = #{isAble,jdbcType=TINYINT}


		AND ABLE_TIME = #{ableTime,jdbcType=TIMESTAMP}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

 WHERE DELETED = "F"
